document.addEventListener("DOMContentLoaded",function(){const e=document.querySelector(".menu-toggle"),n=document.querySelector("header nav ul");e&&n&&(e.addEventListener("click",function(){n.classList.toggle("open")}),n.querySelectorAll("a").forEach(function(e){e.addEventListener("click",function(){n.classList.contains("open")&&n.classList.remove("open")})}))});